        map.removeLayer("markers4");
        map.removeSource("entidades");
      }

      map.addSource("entidades", { type: "geojson", data: this.entidades });

      map.addLayer({
        id: "blocos",
        type: "fill",
        source: "blocos",
        layout: {
          visibility: "visible",
