        layout: {
          visibility: "visible",
        },
        paint: {
          "fill-color": "rgb(60, 233, 36)",
          "fill-opacity": [
            "case",
            ["boolean", ["feature-state", "hover"], false],
            0.32,
            0.18,
          ],
        },
      });
      map.addLayer({
        id: "unidades-line",
        type: "line",
        source: "unidades",
        layout: {
          visibility: "visible",
        },
        paint: {
          "line-color": "rgb(0,0,0)",
          "line-width": [
            "case",
            ["boolean", ["feature-state", "hover"], false],
            1.5,
            1,
          ],
        },
      });
      map.addLayer({
        id: "municipios",
        type: "fill",
        source: "municipios",
        layout: {
          visibility: "visible",
        },
        paint: {
          "fill-color": "rgb(245, 40, 145)",
          "fill-opacity": [
            "case",
            ["boolean", ["feature-state", "hover"], false],
            0.1,
            0.1,
          ],
        },
      });
      map.addLayer({
        id: "municipios-line",
        type: "line",
        source: "municipios",
        layout: {
          visibility: "visible",
        },
        paint: {
          "line-color": "rgb(0,0,0)",
          "line-width": [
            "case",
            ["boolean", ["feature-state", "hover"], false],
            1.5,
            1,
          ],
        },
      });

      map.addLayer({
        id: "campos",
        type: "fill",
        source: "campos",
        layout: {
          visibility: "visible",
        },
        paint: {
          "fill-color": "rgb(245, 40, 145)",
          "fill-opacity": [
            "case",
            ["boolean", ["feature-state", "hover"], false],
            0.5,
            0.8,
