      // map.addLayer({
      //   id: "bacia",
      //   type: "fill",
      //   source: "bacia",
      //   paint: {
      //     "fill-color": "rgb(245, 40, 145)",
      //     "fill-opacity": [
      //       "case",
      //       ["boolean", ["feature-state", "hover"], false],
      //       0.001,
      //       0.001,
      //     ],
      //   },
      // });
      map.addLayer({
        id: "bacia-line",
