        source: "bacia-sedimentar",
        layout: {
          visibility: "none",
        },
        paint: {
          "line-color": "rgb(0,0,0)",
          "line-width": [
            "case",
            ["boolean", ["feature-state", "hover"], false],
            1.5,
            1,
          ],
        },
      });

      // pescadores
      map.addLayer({
        id: "markers2",
        type: "symbol",
        source: "pescadores",
        paint: {
          "text-color": "#fff",
        },
        layout: {
          "icon-image": "pescadores-icon",
          "icon-size": 0.7,
          "icon-ignore-placement": false,
          "icon-allow-overlap": false,
          visibility: "visible",
        },
      });

      map.addLayer({
        id: "markers3",
        type: "symbol",
        source: "ilhas",
        paint: {
          "text-color": "#fff",
        },
        layout: {
          "icon-image": "island",
          "icon-size": 0.1,
          "icon-ignore-placement": false,
          visibility: "visible",
        },
      });

      map.addLayer({
        id: "markers4",
